import React, { useState, useEffect } from "react";
import { AppData } from "./appdata";
import SectionTools from "./sectionTools";
import Gallery from "./gallery";
import helpers from "./helpers";

const Skewl = props => {
  let { state, dispatch } = React.useContext(AppData);
  const skewl = props.skewl;
  const logoPath = "assets/img/logos/" + skewl.logo;

  return (
    <React.Fragment>
      <li class="rr add-tack">
        <span class="wrap">
          <h2>
            {skewl.name} <em>({skewl.type})</em>
          </h2>
          <img src={logoPath} alt={skewl.name} />
          <dl class="pretty-scroll">
            <dt>Location: </dt>
            <dd>{skewl.location}</dd>
            <dt>Dates: </dt>
            <dd>
              {skewl.start} - {skewl.end}
            </dd>
            <dt>Degree: </dt>
            <dd>{skewl.degree}</dd>
            <dt>Awards: </dt>
            <dd>{skewl.awards}</dd>
            <dt>Detail: </dt>
            <dd>{skewl.description}</dd>
          </dl>
        </span>
      </li>
    </React.Fragment>
  );
};
export default Skewl;
