import * as React from "react";
import ReactDOM from "react-dom";
import { AppData } from "./appdata";

const google = window.google;

class Commute extends React.Component {
  static contextType = AppData; // ***** access via this.context

  constructor(props) {
    super(props);
    this.state = {
      mapIsReady: false,
      myLoc: {},
      googleMap: {},
      jeffMarker: {},
      jeffInfo: {}
    };
    //const google = window.google;
  }
  componentWillMount() {
    window.onload = this.setState({
      mapIsReady: true,
      myLoc: this.context.myLatLng
    });
  }
  componentDidMount() {
    if (this.state.mapIsReady) {
      // Display the map
      this.map = new google.maps.Map(document.getElementById("Commute"), {
        center: this.state.myLoc,
        zoom: 14,
        disableDefaultUI: true,
        backgroundColor: "hsla(0, 0%, 0%, 0)",
        mapTypeControlOptions: {
          mapTypeIds: [
            "roadmap",
            "satellite",
            "hybrid",
            "terrain",
            "styled_map"
          ]
        }
      });
      var map = this.map;
      console.log("We have a map, set marker, etc...");
      // You also can add markers on the map below

      var contentString = '<div id="mapContent"><h4>Me</h4></div>';
      this.infowindow = new google.maps.InfoWindow({
        content: contentString
      });
      var infowindow = this.infowindow;
      this.marker = new google.maps.Marker({
        position: { lat: 42.4316604, lng: -83.484051 },
        map: map,
        title: "Me",
        animation: google.maps.Animation.DROP,
        icon: {
          url:
            "https://icons.iconarchive.com/icons/icons-land/vista-map-markers/128/Map-Marker-Push-Pin-2-Right-Chartreuse-icon.png",
          scaledSize: new google.maps.Size(64, 64)
        }
      });
      var marker = this.marker;
      google.maps.event.addListener(marker, "click", function() {
        infowindow.open(map, this);
      });

      this.setState({
        googleMap: map,
        jeffMarker: marker,
        jeffInfo: infowindow
      });

      this.updateMapStyle();
    }
  }

  componentWillUpdate(nextProps, nextState) {
    console.log("Component Will Update");
  }
  componentDidUpdate(prevProps, prevState) {
    console.log("Component Did Update");
  }

  updateMapStyle = () => {
    console.log(this.state.googleMap);

    this.googleMapRef.current.mapTypes.set("styled_map", BobsMyUncle); // Get that sweet map style
    this.googleMapRef.current.setMapTypeId("styled_map");

    const BobsMyUncle = this.googleMapRef.current.StyledMapType(
      [
        {
          featureType: "all",
          elementType: "all",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "administrative.country",
          elementType: "all",
          stylers: [{ visibility: "on" }]
        },
        {
          featureType: "administrative.country",
          elementType: "labels.text.fill",
          stylers: [{ color: "#ff0000" }]
        },
        {
          featureType: "administrative.country",
          elementType: "labels.text.stroke",
          stylers: [{ color: "#3a3535" }]
        },
        {
          featureType: "administrative.province",
          elementType: "all",
          stylers: [{ visibility: "on" }]
        },
        {
          featureType: "administrative.province",
          elementType: "labels.text.fill",
          stylers: [{ color: "#ff0000" }]
        },
        {
          featureType: "administrative.province",
          elementType: "labels.text.stroke",
          stylers: [{ color: "#3a3535" }]
        },
        {
          featureType: "administrative.locality",
          elementType: "all",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "administrative.locality",
          elementType: "labels.text.fill",
          stylers: [{ color: "#ffffff" }]
        },
        {
          featureType: "administrative.locality",
          elementType: "labels.text.stroke",
          stylers: [
            { color: "#756151" },
            { lightness: "15" },
            { saturation: "2" },
            { gamma: "3.67" }
          ]
        },
        {
          featureType: "road.highway",
          elementType: "all",
          stylers: [{ visibility: "simplified" }]
        },
        {
          featureType: "road.highway",
          elementType: "geometry.fill",
          stylers: [{ visibility: "simplified" }, { color: "#f0af54" }]
        },
        {
          featureType: "road.highway",
          elementType: "labels.text",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "road.highway",
          elementType: "labels.icon",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "road.highway.controlled_access",
          elementType: "all",
          stylers: [{ visibility: "simplified" }]
        },
        {
          featureType: "road.highway.controlled_access",
          elementType: "geometry.fill",
          stylers: [{ visibility: "simplified" }, { color: "#f0af54" }]
        },
        {
          featureType: "road.highway.controlled_access",
          elementType: "labels.text",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "road.highway.controlled_access",
          elementType: "labels.icon",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "road.arterial",
          elementType: "all",
          stylers: [{ visibility: "simplified" }]
        },
        {
          featureType: "road.arterial",
          elementType: "geometry.fill",
          stylers: [{ visibility: "simplified" }, { color: "#d2c1a5" }]
        },
        {
          featureType: "road.arterial",
          elementType: "labels",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "road.local",
          elementType: "all",
          stylers: [{ visibility: "simplified" }]
        },
        {
          featureType: "road.local",
          elementType: "geometry.fill",
          stylers: [{ color: "#c4a983" }, { visibility: "simplified" }]
        },
        {
          featureType: "road.local",
          elementType: "labels",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "water",
          elementType: "all",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "water",
          elementType: "geometry",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "water",
          elementType: "geometry.fill",
          stylers: [{ visibility: "off" }, { color: "#e3ce9f" }]
        },
        {
          featureType: "water",
          elementType: "geometry.stroke",
          stylers: [{ visibility: "simplified" }]
        },
        {
          featureType: "water",
          elementType: "labels",
          stylers: [{ visibility: "off" }]
        }
      ],
      { name: "bobs-my-uncle" }
    );
  };
  /*

	infoWindow = new google.maps.InfoWindow;
	var contentString = '<div id="mapContent"><h4>Me</h4></div>';
	var infowindow = new google.maps.InfoWindow({ content: contentString });
	var marker = new google.maps.Marker({
		position: myLatLng,
		map: map,
		title: 'Me',
		animation: google.maps.Animation.DROP,
		icon: {
			url: "https://icons.iconarchive.com/icons/icons-land/vista-map-markers/128/Map-Marker-Push-Pin-2-Right-Chartreuse-icon.png",
			scaledSize: new google.maps.Size(64, 64)
		}
