import React, { useState, useEffect } from "react";
import { AppData } from "./appdata";
import SectionTools from "./sectionTools";
import SkillTabs from "./skillTabs";
import helpers from "./helpers";

const Skills = () => {
  let { state, dispatch } = React.useContext(AppData);
  const skillCategories = state.resume.skills;
  const [skillCats, setSkillCats] = useState(skillCategories);

  return (
    <React.Fragment>
      <section className="res-skills res-editable" id="Skills">
        <h2 className="taped-b rr poly-wrap">Skills</h2>
        <ul className="">
          {skillCats.map((skillCat, index) => {
            const skillCatType = skillCat.type;
            let skillCatItems = skillCat[index];
            const skillTypeClass =
              "res-editable res-skills-" + helpers.classify(skillCat.type);
            return (
              <li className={skillTypeClass} key={index} skillCat={skillCat}>
                <h3 className="tabs-title tabby tack rr">
                  <i className="inner"></i>
                  <span>{skillCatType}:</span>
                  <SectionTools />
                </h3>
                <ul
                  data-type={skillCatType}
                  className="taped-b tabs"
                  role="tablist"
                  data-radio="skillTabs"
                >
                  {/* 
                  {skillCatItems.map((skillCatItem, index) => {
                    <SkillTabs
                      key={index}
                      id={index}
                      skillCatItem={skillCatItem}
                    />;
                  })}*/}
                </ul>
              </li>
            );
          })}
        </ul>
      </section>
    </React.Fragment>
  );
};

export default Skills;
