import * as React from "react";
import { AppData } from "./appdata";
import Employer from "./employer";
import helpers from "./helpers";

class History extends React.Component {
  static contextType = AppData; // ***** access via this.context

  constructor(props) {
    super(props);
    this.state = {
      balls: "",
      empCount: 0,
      openDeets: 0,
      toggleClass: "toggle-all"
    };

    this.toggleDetails = this.toggleDetails.bind(this);
    this.handleChildDeets = this.handleChildDeets.bind(this);
    this.heyBattaBatta = this.heyBattaBatta.bind(this);
  }

  componentDidMount() {
    const balls = "balls";

    this.setState({
      balls: balls
    });
  }
  componentWillUpdate(nextProps, nextState) {}
  componentDidUpdate(prevProps, prevState) {}

  handleChildDeets(event) {
    // the event context comes from the Child
    this.setState({});
    console.log("empCount: " + this.state.empCount);
    console.log(
      "articleId: " + event.target.closest("article").getAttribute("id")
    );
    // returns pre-click value:
    console.log("detailsEl: " + event.target.closest("details").open);
    // set w/ detailsObject.open = true|false
    const opening = !event.target.closest("details").open;
    opening
      ? this.setState({
          openDeets: this.state.openDeets + 1
        })
      : this.setState({
          openDeets: this.state.openDeets - 1
        });
    this.state.openDeets > 0
      ? this.setState({
          toggleClass: "toggle-all active"
        })
      : this.setState({
          toggleClass: "toggle-all"
        });

    this.heyBattaBatta();
    /*
      $(this).closest('details').prop('open') ? open-- : open++;
      open > 0 ? $('#History h2 span').addClass('active') : $('#History h2 span').removeClass('active');
      heyBattaBatta($('#History time'));
    */
  }

  toggleDetails(event) {
    event.preventDefault();
    let total = this.state.empCount;
    let open = this.state.openDeets;

    /*
      e.preventDefault(); 
      if ($(this).hasClass('active') && open !== 0){
        $('#History details').prop('open', false); //.closest('article').removeClass('no-dog-ear');
        $(this).removeClass('active');
      } else {
        $('#History details').prop('open', true); //.closest('article').addClass('no-dog-ear');
        $(this).addClass('active');
      }
      heyBattaBatta($('#History time'));
    */
  }

  heyBattaBatta() {
    const swingas = document.getElementsByTagName("time");
    //swingas.classList.remove("swing");
    for (let swinga of swingas) {
      swinga.classList.remove("swing");
      swinga.setAttribute(
        "style",
        "animation-delay: " + helpers.getRandom(1) / 2 + "s"
      );
      swinga.classList.add("swing");
    }
    setTimeout(function() {
      swingas.classList.remove("swing");
    }, 5000);
  }

  /*
   var heyBattaBatta = function(swingas){
    swingas.removeClass('swing');
    $.each(swingas, function( i, s ) {
      $(this).addClass('swing').css('animation-delay', (getRandom(1) / 2) + 's');
    });
    setTimeout(function(){ swingas.removeClass('swing'); }, 5000);
  }
  */

  render() {
    const context = this.context;
    const employerList = context.resume.history;
    const toggleClass = this.state.toggleClass;
    return (
      <React.Fragment>
        <section className="res-history" id="History">
          <h2 className="note toggle-history-deets rr">
            Professional History{" "}
            <span
              className={toggleClass}
              onClick={e => {
                this.toggleDetails(e);
              }}
            ></span>
          </h2>
          {employerList.map((employer, index) => {
            this.state.empCount = index + 1;
            return (
              <Employer
                key={index}
                id={index}
                employer={employer}
                deetHandler={e => {
                  this.handleChildDeets(e);
                }}
              />
            );
          })}
        </section>
      </React.Fragment>
    );
  }
}

export default History;
