import * as React from "react";
import { AppData } from "./appdata";

class Condensificator extends React.Component {
  constructor(props) {
    super(props);
    this.state = { isCondensed: false };
  }
  /*
$(document).on('click', '.condensificator', function(e) {
	// so hacky!
	var origText = '<i class="fas fa-eye-slash"></i> Brevity Please';
	var altText = '<i class="fas fa-eye"></i> Oh that\'s novel. Now put it back.';
	$(this).closest('.step').toggleClass('condensed');
	$(this).closest('.condensed')? $(this).html(altText) : $(this).html(origText) ;
});
*/
  render() {
    return (
      <span
        className="condensificator test-me"
        data-test-name="Intro: Condense Slide 1"
        data-test-cat="Intro Nav"
      >
        <i className="fas fa-eye-slash"></i> Brevity Please
      </span>
    );
  }
}

export default Condensificator;
