import * as React from "react";
import ReactDOM from "react-dom";
import { AppData } from "./appdata";
import { Map, InfoWindow, GoogleApiWrapper, Marker } from "google-maps-react";

class Commute extends React.Component {
  static contextType = AppData; // ***** access via this.context
  /*
  state = {
    data: [],
    loc_x: 0,
    loc_y: 0,
    userLL: {},
    locRendered: false,
    showingInfoWindow: false,
    activeMarker: {},
    selectedPlace: {}
  };
  */

  constructor(props) {
    super(props);
    this.state = {
      data: [],
      loc_x: 0,
      loc_y: 0,
      userLL: {},
      locRendered: false,
      showingInfoWindow: false,
      activeMarker: {},
      selectedPlace: {}
    };
    this.onMarkerClick = this.onMarkerClick.bind(this);
    this.onMapClicked = this.onMapClicked.bind(this);
    this.centerMoved = this.centerMoved.bind(this);
  }

  getUserLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(this.showPosition);
    }
  };

  showPosition = position => {
    this.setState({
      loc_x: position.coords.latitude,
      loc_y: position.coords.longitude,
      locRendered: true,
      userLL: { lat: position.coords.latitude, lng: position.coords.longitude }
    });
    console.log(position);
  };

  componentWillMount() {
    this.getUserLocation();
  }
  componentDidMount() {}
  componentWillUpdate(nextProps, nextState) {}
  componentDidUpdate(prevProps, prevState) {}

  onMarkerClick = (props, marker, e) => {
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });
  };
  onMapClicked = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };

  centerMoved(mapProps, map) {
    console.log("shit was moved");
  }

  onMouseoverMarker() {
    console.log("marker mouseover");
  }
  onInfoWindowClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };

  windowHasOpened() {
    console.log("open window");
  }
  windowHasClosed() {
    console.log("close window");
  }

  render() {
    //console.log(this.context.introVisible);
    const context = this.context;

    const mapActiveClass = context.mapVisible
      ? "res-map full-view active"
      : "res-map full-view";

    const containerStyle = {
      backgroundColor: "hsla(0, 0%, 0%, 0)",
      width: "100%",
      height: "100%"
    };
    const google = this.props.google;
    //console.log(this.props.mapStyles);

    return (
      <div id="Map" className={mapActiveClass}>
        <div id="Commute">
          <Map
            google={google}
            zoom={14}
            style={containerStyle}
            styles={this.props.mapStyles}
            containerStyle={containerStyle}
            initialCenter={context.myLatLng}
            disableDefaultUI={true}
            backgroundColor="hsla(0, 0%, 0%, 0)"
            onClick={this.onMapClicked}
            onDragend={this.centerMoved}
            mapTypeControlOptions={{
              mapTypeIds: [
                "roadmap",
                "satellite",
                "hybrid",
                "terrain",
                "styled_map"
              ]
            }}
          >
            <Marker
              position={context.myLatLng}
              title="Me"
              animation={google.maps.Animation.DROP}
              icon={{
                url:
                  "https://icons.iconarchive.com/icons/icons-land/vista-map-markers/128/Map-Marker-Push-Pin-2-Right-Chartreuse-icon.png",
                scaledSize: new google.maps.Size(64, 64)
              }}
              onClick={this.onMarkerClick}
              onMouseover={this.onMouseoverMarker}
              name={"My location"}
            >
              <InfoWindow
                marker={this.state.activeMarker}
                onOpen={this.windowHasOpened}
                onClose={this.windowHasClosed}
                visible={this.state.showingInfoWindow}
              >
                <div>
                  <h1>{this.state.selectedPlace.name}</h1>
                </div>
              </InfoWindow>
            </Marker>
            {/* 
            <Marker
              position={this.state.myLatLng}
              title="You"
              animation={google.maps.Animation.DROP}
              icon={{
                url:
                  "https://icons.iconarchive.com/icons/icons-land/vista-map-markers/256/Map-Marker-Push-Pin-2-Left-Azure-icon.png",
                scaledSize: new google.maps.Size(64, 64)
              }}
              onClick={this.onMarkerClick}
              onMouseover={this.onMouseoverMarker}
              name={"Your location"}
            >
              <InfoWindow
                marker={this.state.activeMarker}
                onOpen={this.windowHasOpened}
                onClose={this.windowHasClosed}
                visible={this.state.showingInfoWindow}
              >
                <div>
                  <h1>{this.state.selectedPlace.name}</h1>
                </div>
              </InfoWindow>
            </Marker>*/}
          </Map>
        </div>
        <div className="res-map-info">
          <p className="thinking">Calculating my commute to you.</p>
        </div>
      </div>
    );
  }
}

