import React, { useState, useEffect } from "react";
import { AppData } from "./appdata";
import Employer from "./employer";

class Employers extends React.Component {
  static contextType = AppData; // ***** access via this.context

  render() {
    const context = this.context;
    const employerList = context.resume.history;
    const [employers, setEmployers] = useState(employerList);

    return (
      <React.Fragment>
        {employers.map((employer, index) => {
          return <Employer key={index} id={index} employer={employer} />;
        })}
      </React.Fragment>
    );
  }
}

export default Employers;
