import * as React from "react";
import { AppData } from "./appdata";
import SkillTable from "./skillTable";
import SkillChart from "./skillChart";
import SkillStars from "./skillStars";
import SkillInfo from "./skillInfo";

const SkillTab = props => {
  let { state, dispatch } = React.useContext(AppData);
  const tabtype = props.tabtype;
  const tabdata = props.skilldata;
  const chartUI = "chart...";
  const tableUI = "table...";
  const starsUI = "stars...";
  const infoUI = "info...";
  //console.log(tabdata);

  switch (tabtype) {
    case "chart":
      return <SkillChart />;
    case "table":
      return <SkillTable />;
    case "stars":
      return <SkillStars />;
    case "info":
      return <SkillInfo />;
    default:
      return null;
  }
};
