class Intro extends React.Component {
  static contextType = AppData; // ***** access via this.context

  state = {
    introVisible: false,
    steps: [],
    totalSteps: 0,
    currentStep: 0,
    prevTitle: "",
    nextTitle: ""
  };
  constructor(props) {
    super(props);
    this.state = {
      /*
      introVisible: true,
      steps: React.Children.toArray(this.props.children).filter(
        item => item.props.className === "step"
      ),
      totalSteps: steps.length,
      currentStep: 0,
      prevTitle: steps[currentStep - 1].getElementByTagName("header").innerText,
      nextTitle: steps[currentStep + 1].getElementByTagName("header").innerText
      */
    };

    this.closeIntro = this.closeIntro.bind(this);
    /*
    this.stepOnIt = this.stepOnIt.bind(this);
    this.getSteppin = this.getSteppin.bind(this);
    this.stepAlong = this.stepAlong.bind(this);

    this.nextSlide = this.nextSlide.bind(this);
    this.previousSlide = this.previousSlide.bind(this);
    */
  }

  /*
  stepOnIt(event){ // $(document).on('click', '.step-off h2 a', function(e) {
    event.preventDefault();

    $('.section-'+currentStep).removeClass('current');
    $(this).is('.step-prev') ? currentStep-- : currentStep++;
    $('.section-'+currentStep).addClass('current');
    $('.step-off').attr('data-current-section', currentStep);
    $('.step-off .status').text(currentStep + ' / ' + $('.step-off .step').length);
    stepAlong();
  }
  getSteppin(){
    $('.step-off').attr('data-current-section', currentStep);
    $('.step-off .section-1').addClass('current');
    $('.step-off .status').text('1 / ' + $('.step-off .step').length);
    stepAlong();
  }
  stepAlong(){
    $('.step-off a.step-prev, .step-off a.step-next').hide();
    if (currentStep > 1){
      prevTitle = $('.step-off .section-' + (currentStep - 1) + ' header').text();
      $('.step-off a.step-prev').show();
    } else {
      prevTitle = '';
    }
    if (currentStep < $('.step-off .step').length){
      nextTitle = $('.step-off .section-' + (currentStep + 1) + ' header').text();
      $('.step-off a.step-next').show();
    } else {
      nextTitle = '';
    }
    $('.step-off a.step-prev i').text(prevTitle);
    $('.step-off a.step-next i').text(nextTitle);
  }
*/
  componentDidMount(prevProps, prevState) {}
  componentDidUpdate(prevProps, prevState) {
    //document.body.classList.remove(prevState.currentTheme);
    //document.body.classList.add(this.state.currentTheme);
  }

  closeIntro(event) {
    document.getElementById("HEY").classList.remove("active");
  }
  /*
  nextSlide(event) {
    console.log("next");

    $('.section-'+currentStep).removeClass('current');
    $(this).is('.step-prev') ? currentStep-- : currentStep++;
    $('.section-'+currentStep).addClass('current');
    $('.step-off').attr('data-current-section', currentStep);
    $('.step-off .status').text(currentStep + ' / ' + $('.step-off .step').length);
    stepAlong();

  }
  previousSlide(event) {
    console.log("previous");
  }
*/
  render() {
    let introData = IntroContent.introData;
    return (
      <AppData.Consumer>
        {context => {
          const {
            currentTheme,
            introVisible,
            menuVisible,
            myGlobalFunction
          } = context.state;
          let toggleIntro = event =>
            context.dispatch({ type: "toggle-intro", payload: "BALLS" });
          //console.log(introVisible);
          const introActiveClass = introVisible
            ? "tooltip btfu active"
            : "tooltip btfu";
          return (
            <div id="HEY" data-current-section="1" className="step-off active">
              <a
                href="#"
                className="close-the-hey test-me"
                data-test-name="Intro: Close"
                data-test-cat="Intro Nav"
                onClick={e => {
                  this.closeIntro(e);
                }}
              >
                Close
              </a>
              <h2 className="">
                HEY!
                <a
                  href="#"
                  className="step-prev test-me"
                  data-test-name="Intro: Previous"
                  data-test-cat="Intro Nav"
                  onClick={e => {
                    this.previousSlide(e);
                  }}
                >
                  Back to: <i></i>
                </a>
                <a
                  href="#"
                  className="step-next test-me"
                  data-test-name="Intro: Next"
                  data-test-cat="Intro Nav"
                  onClick={e => {
                    this.nextSlide(e);
                  }}
                >
                  Next up: <i></i>
                </a>
                <span className="status"></span>
              </h2>

              {introData.map((slide, index) => {
                var slideClass = "step section-" + index;
                slideClass += index === 0 ? " current" : "";
                return (
                  <section key={index} className={slideClass}>
                    <header>{slide.title}</header>
                    <div
                      className="pretty-scroll"
                      dangerouslySetInnerHTML={{ __html: slide.text }}
                    ></div>
                    <Condensificator />
                  </section>
                );
              })}
            </div>
          );
        }}
      </AppData.Consumer>
    );
  }
}

export default Intro;
