  componentDidMount() {
    const allSteps = document
      .getElementById("HEY")
      .getElementsByClassName("step");
    this.setState({ totalSteps: allSteps.length, steps: allSteps });
  }
  componentDidUpdate(prevProps, prevState) {
    //document.body.classList.remove(prevState.currentTheme);
    //document.body.classList.add(this.state.currentTheme);
  }

  closeIntro(event) {
    const intro = document.getElementById("HEY");
    const menuItem = document.getElementsByClassName("btfu")[0];
    intro.classList.remove("active");
    menuItem.classList.remove("active");
    this.setState({ introVisible: false });
  }
  nextStep(event) {
    event.preventDefault();
  }
  previousStep(event) {
    event.preventDefault();
  }
  updateStatus(event) {}

  /*
  nextSlide(event) {
    console.log("next");

    $('.section-'+currentStep).removeClass('current');
    $(this).is('.step-prev') ? currentStep-- : currentStep++;
    $('.section-'+currentStep).addClass('current');
    $('.step-off').attr('data-current-section', currentStep);
    $('.step-off .status').text(currentStep + ' / ' + $('.step-off .step').length);
    stepAlong();

  }
  previousSlide(event) {
    console.log("previous");
  }
*/
  render() {
    //console.log(this.context.introVisible);
    let introData = IntroContent.introData;
    const context = this.context;

    const introActiveClass = context.introVisible
      ? "step-off active"
      : "step-off";

    return (
      <div id="HEY" data-current-section="1" className={introActiveClass}>
        <a
          href="#"
          className="close-the-hey test-me"
          data-test-name="Intro: Close"
          data-test-cat="Intro Nav"
          onClick={e => {
            this.closeIntro(e);
            context.toggleIntro(e, false);
          }}
        >
          Close
        </a>
        <h2 className="">
          HEY!
          <a
            href="#"
            className="step-prev test-me"
            data-test-name="Intro: Previous"
            data-test-cat="Intro Nav"
            onClick={e => {
              this.previousStep(e);
            }}
          >
            Back to: <i></i>
          </a>
          <a
            href="#"
            className="step-next test-me"
            data-test-name="Intro: Next"
            data-test-cat="Intro Nav"
            onClick={e => {
              this.nextStep(e);
            }}
          >
            Next up: <i></i>
          </a>
          <span className="status"></span>
        </h2>

        {introData.map((slide, index) => {
          var slideClass = "step section-" + index;
          slideClass += index === 0 ? " current" : "";
          return (
            <section key={index} className={slideClass}>
              <header>{slide.title}</header>
              <div
                className="pretty-scroll"
                dangerouslySetInnerHTML={{ __html: slide.text }}
              ></div>
              <Condensificator />
            </section>
          );
        })}
      </div>
    );
  }
}

export default Intro;
