import * as React from "react";
import { AppData } from "./appdata";
import helpers from "./helpers";

const axios = require("axios").default;

class Debugger extends React.Component {
static contextType = AppData; // ***** access via this.context

  constructor(props){
    super(props);
    this.state = {
      ua: {},
      ip: "",
      ipData: {}
    };
    this.escFunction = this.escFunction.bind(this);
  }
  escFunction(event){
    if(event.keyCode === 27) {
      //Do whatever when esc is pressed
      alert(this.state.ip);
    }
  }

  componentDidMount(){
    var userIp = "";
    var ipAddress = "";
    var ipData = {};
    var self = this;
    var http = require('http');

    var uaGetString = "http://api.userstack.com/detect?access_key=b301331f9e5761edf368d241475b4836&ua=" + navigator.userAgent;
    axios.get(uaGetString)
    .then(function (response) {
        console.log(response);
        self.setState({ ua: response.data })
    })
    .catch(function (error) {
        console.log(error);
        self.setState({ ua: null })
    })
    .finally(function () {
      // always executed
    });

    http.get({'host': 'api.ipify.org', 'port': 80, 'path': '/'}, function(resp) {
      resp.on('data', function(ip) {
        console.log("My public IP address is: " + ip);
        console.log(ip);
        ipAddress = ip;

        var ipGetString = "http://api.ipstack.com/" + ip + "?access_key=440420267b2d228f92803e8a2d074666"
        axios.get(ipGetString)
        .then(function (response) {
            console.log(response);
            self.setState({ ip: ipAddress, ipData: response.data })
        })
        .catch(function (error) {
            console.log(error);
            self.setState({ ip: null, ipData: null })
        })
        .finally(function () {
        // always executed
        });

      });
    });

    console.log(helpers.getScreenData());

    document.addEventListener("keydown", this.escFunction, false);
  }
  componentWillUnmount(){
    document.removeEventListener("keydown", this.escFunction, false);
  }
  render(){

    return (   
      <div>{this.state.ip}</div>
    )
  }
}

export default Debugger;
