import * as React from "react";
import ReactDOM from "react-dom";
import { AppData } from "./appdata";

class Commute extends React.Component {
  static contextType = AppData; // ***** access via this.context

  constructor(props) {
    super(props);
    this.state = {
      myLoc: {},
      googleMap: {},
      jeffMarker: {}
    };
    this.googleMapRef = React.createRef();
  }
  componentDidMount() {
    this.setState({ myLoc: this.context.myLatLng });
    const googleMapScript = document.createElement("script");
    googleMapScript.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyBl1YIXtw0RdoIw7Qo5h7Q32IIy3zkw6C0`;
    window.document.body.appendChild(googleMapScript);
    googleMapScript.addEventListener("load", () => {
      this.googleMap = this.createGoogleMap();
      this.marker = this.createMarker();
      this.mapStyle = this.updateMapStyle();
    });
  }

  componentWillUpdate(nextProps, nextState) {}
  componentDidUpdate(prevProps, prevState) {}

  createGoogleMap = () => {
    const BobsMyUncle = new window.google.maps.Map.StyledMapType(
      [
        {
          featureType: "all",
          elementType: "all",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "administrative.country",
          elementType: "all",
          stylers: [{ visibility: "on" }]
        },
        {
          featureType: "administrative.country",
          elementType: "labels.text.fill",
          stylers: [{ color: "#ff0000" }]
        },
        {
          featureType: "administrative.country",
          elementType: "labels.text.stroke",
          stylers: [{ color: "#3a3535" }]
        },
        {
          featureType: "administrative.province",
          elementType: "all",
          stylers: [{ visibility: "on" }]
        },
        {
          featureType: "administrative.province",
          elementType: "labels.text.fill",
          stylers: [{ color: "#ff0000" }]
        },
        {
          featureType: "administrative.province",
          elementType: "labels.text.stroke",
          stylers: [{ color: "#3a3535" }]
        },
        {
          featureType: "administrative.locality",
          elementType: "all",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "administrative.locality",
          elementType: "labels.text.fill",
          stylers: [{ color: "#ffffff" }]
        },
        {
          featureType: "administrative.locality",
          elementType: "labels.text.stroke",
          stylers: [
            { color: "#756151" },
            { lightness: "15" },
            { saturation: "2" },
            { gamma: "3.67" }
          ]
        },
        {
          featureType: "road.highway",
          elementType: "all",
          stylers: [{ visibility: "simplified" }]
        },
        {
          featureType: "road.highway",
          elementType: "geometry.fill",
          stylers: [{ visibility: "simplified" }, { color: "#f0af54" }]
        },
        {
          featureType: "road.highway",
          elementType: "labels.text",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "road.highway",
          elementType: "labels.icon",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "road.highway.controlled_access",
          elementType: "all",
          stylers: [{ visibility: "simplified" }]
        },
        {
          featureType: "road.highway.controlled_access",
          elementType: "geometry.fill",
          stylers: [{ visibility: "simplified" }, { color: "#f0af54" }]
        },
        {
          featureType: "road.highway.controlled_access",
          elementType: "labels.text",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "road.highway.controlled_access",
          elementType: "labels.icon",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "road.arterial",
          elementType: "all",
          stylers: [{ visibility: "simplified" }]
        },
        {
          featureType: "road.arterial",
          elementType: "geometry.fill",
          stylers: [{ visibility: "simplified" }, { color: "#d2c1a5" }]
        },
        {
          featureType: "road.arterial",
          elementType: "labels",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "road.local",
          elementType: "all",
          stylers: [{ visibility: "simplified" }]
        },
        {
          featureType: "road.local",
          elementType: "geometry.fill",
          stylers: [{ color: "#c4a983" }, { visibility: "simplified" }]
        },
        {
          featureType: "road.local",
          elementType: "labels",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "water",
          elementType: "all",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "water",
          elementType: "geometry",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "water",
          elementType: "geometry.fill",
          stylers: [{ visibility: "off" }, { color: "#e3ce9f" }]
        },
        {
          featureType: "water",
          elementType: "geometry.stroke",
          stylers: [{ visibility: "simplified" }]
        },
        {
          featureType: "water",
          elementType: "labels",
          stylers: [{ visibility: "off" }]
        }
      ],
      { name: "bobs-my-uncle" }
    );

    const myMap = new window.google.maps.Map(this.googleMapRef.current, {
      zoom: 14,
      center: this.state.myLoc,
      disableDefaultUI: true,
      backgroundColor: "hsla(0, 0%, 0%, 0)",
      mapTypeControlOptions: {
        mapTypeIds: ["roadmap", "satellite", "hybrid", "terrain", "styled_map"]
      }
    });

    myMap.mapTypes.set("styled_map", BobsMyUncle); // Get that sweet map style
    myMap.setMapTypeId("styled_map");

    this.setState({ googleMap: myMap });
  };
  /*

	infoWindow = new google.maps.InfoWindow;

	var contentString = '<div id="mapContent"><h4>Me</h4></div>';
	var infowindow = new google.maps.InfoWindow({ content: contentString });
	var marker = new google.maps.Marker({
		position: myLatLng,
		map: map,
		title: 'Me',
		animation: google.maps.Animation.DROP,
		icon: {
			url: "https://icons.iconarchive.com/icons/icons-land/vista-map-markers/128/Map-Marker-Push-Pin-2-Right-Chartreuse-icon.png",
			scaledSize: new google.maps.Size(64, 64)
		}
	});
  */
  createMarker = () => {
    console.log(this.state.myLoc);
    const myMarker = new window.google.maps.Marker({
      position: this.state.myLoc,
      map: this.googleMap,
      title: "Me",
      animation: window.google.maps.Animation.DROP,
      icon: {
        url:
          "https://icons.iconarchive.com/icons/icons-land/vista-map-markers/128/Map-Marker-Push-Pin-2-Right-Chartreuse-icon.png",
        scaledSize: new window.google.maps.Size(64, 64)
      }
    });
    this.setState({ jeffMarker: myMarker });
  };
  updateMapStyle = () => {
    console.log("update style here");
  };

  render() {
    //console.log(this.context.introVisible);
    const context = this.context;

    const mapActiveClass = context.mapVisible
      ? "res-map full-view active"
      : "res-map full-view";

    return (
      <div id="Map" className={mapActiveClass}>
        <div
          id="Commute"
          ref={this.googleMapRef}
          style={{ width: "100%", height: "100%" }}
        ></div>
        <div className="res-map-info">
          <p className="thinking">Calculating my commute to you.</p>
        </div>
      </div>
    );
  }
}

export default Commute;
