import * as React from "react";
import { AppData } from "./appdata";
import SkillTab from "./skillTab";

class SkillTabs extends React.Component {
  static contextType = AppData;
  render() {
    const context = this.context;
    const tabtypes = context.tabTypes;
    //let incrementTabIndex = () => dispatch({ type: "increment-tab-index" });
    let tabCounter = 0;
    //incrementTabIndex();

    const skilllist = this.props.catSkills;
    const listtype = this.props.skilltype;
    const radioGroupName = "skillTabs" + this.id;
    //console.log(state.tabTypes);
    return (
      <React.Fragment>
        {tabtypes.map((tabtype, index) => {
          let tabsClass = "res-skill-" + listtype + " " + tabtype;
          console.log(
            "id from parent loop: " + this.props.id + ", tabIndex: " + index
          );
          let radioTabIndex = parseInt("" + this.props.id + index);
          //let radioId = "tab" + index;
          let radioId = "tab" + radioTabIndex;
          let isChecked = tabtype === "info";
          let tabIcon = "fas fa-" + context.tabIcons[index];
          //let tabContentId = "tab-content" + index;
          let tabContentId = "tab-content" + radioTabIndex;
          console.log("radioId: " + radioId);
          console.log("isChecked: " + isChecked);
          console.log("radioTabIndex: " + radioTabIndex);

          return (
            <li key={index} className={tabsClass}>
              <input
                type="radio"
                name={radioGroupName}
                id={radioId}
                defaultChecked={isChecked}
                tabIndex={radioTabIndex}
              />

              <label htmlFor={radioId} tabIndex={context.tabCounter}>
                <i className={tabIcon}></i>
              </label>
              <div
                id={tabContentId}
                className="tab-content sketchy-border lined thin"
                data-display={listtype}
              >
                <SkillTab
                  key={index}
                  id={index}
                  skilldata={skilllist}
                  tabtype={tabtype}
                />
              </div>
            </li>
          );
        })}
      </React.Fragment>
    );
  }
}

export default SkillTabs;
