import * as React from "react";
import { AppData } from "./appdata";

const SkillTab = props => {
  let { state, dispatch } = React.useContext(AppData);
  const tabtype = props.tabtype;
  const tabdata = props.skilldata;
  const chartUI = "chart...";
  const tableUI = "table...";
  const starsUI = "stars...";
  const infoUI = "info...";
  //console.log(tabdata);

  switch (tabtype) {
    case "chart":
      return <React.Fragment>{chartUI}</React.Fragment>;
    case "table":
      return <React.Fragment>{tableUI}</React.Fragment>;
    case "stars":
      return <React.Fragment>{starsUI}</React.Fragment>;
    case "info":
      return <React.Fragment>{infoUI}</React.Fragment>;
    default:
      return null;
  }
};
