import * as React from "react";
import { AppData } from "./appdata";
import SkillTab from "./skillTab";

const SkillTabs = props => {
  let { state, dispatch } = React.useContext(AppData);
  const tabtypes = state.tabTypes;
  let incrementTabIndex = () => dispatch({ type: "increment-tab-index" });
  let tabCounter = 0;
  //incrementTabIndex();

  const skilllist = props.catSkills;
  const listtype = props.skilltype;
  const radioGroupName = "skillTabs" + props.id;
  //console.log(state.tabTypes);
  return (
    <React.Fragment>
      {tabtypes.map((tabtype, index) => {
        let tabsClass = "res-skill-" + listtype + " " + tabtype;
        let radioId = "tab" + index;
        let isChecked = tabtype === "info";
        let tabIcon = "fas fa-" + state.tabIcons[index];
        let radioTabIndex = parseInt("" + props.id + index);
        let tabContentId = "tab-content" + index;
        console.log(radioTabIndex);

        return (
          <li key={index} className={tabsClass}>
            <input
              type="radio"
              name={radioGroupName}
              id={radioId}
              defaultChecked={isChecked}
              tabIndex={radioTabIndex}
            />

            <label htmlFor={radioId} tabIndex={state.tabCounter}>
              <i className={tabIcon}></i>
            </label>
            <div
              id={tabContentId}
              className="tab-content sketchy-border lined thin"
              data-display={listtype}
            >
              <SkillTab
                key={index}
                id={index}
                skilldata={skilllist}
                tabtype={tabtype}
              />
            </div>
          </li>
        );
      })}
    </React.Fragment>
  );
};

export default SkillTabs;
