import React, { useState, useEffect } from "react";
import { AppData } from "./appdata";

class Recommendations extends React.Component {
  static contextType = AppData; // ***** access via this.context

  render() {
    const { resume } = this.context;
    const kudosList = resume.recommendations;
    const [kudos, setKudo] = useState(kudosList);
    return (
      <React.Fragment>
        <section className="res-blurbs res-editable auto-slides" id="Blurbs">
          <h2 className="note rr poly-wrap">Recommendations</h2>
          <ul className="add-tack">
            {kudos.map((kudo, index) => {
              const napClass = "rr nap" + index;
              return (
                <li key={index} className={napClass}>
                  <blockquote>
                    <p className="can-edit">{kudo.text}</p>
                    <cite className="can-edit">
                      <strong
                        dangerouslySetInnerHTML={{ __html: kudo.name }}
                      ></strong>
                      <br />
                      {kudo.title}
                      <br />
                      {kudo.company}
                    </cite>
                  </blockquote>
                </li>
              );
            })}
          </ul>
        </section>
      </React.Fragment>
    );
  }
}

export default Recommendations;
