import * as React from "react";
import ReactDOM from "react-dom";
import { AppData } from "./appdata";
import { Map, GoogleApiWrapper, Marker } from "google-maps-react";

class Commute extends React.Component {
  static contextType = AppData; // ***** access via this.context

  state = {
    data: [],
    loc_x: 0,
    loc_y: 0,
    locRendered: false,
    showingInfoWindow: false,
    activeMarker: {},
    selectedPlace: {},
    redirect: false,
    redirectId: 0
  };
  constructor(props) {
    super(props);
    this.state = {};
  }

  getUserLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(this.showPosition);
    }
  };

  showPosition = position => {
    this.setState({
      loc_x: position.coords.latitude,
      loc_y: position.coords.longitude,
      locRendered: true
    });
  };

  componentWillMount() {
    this.getUserLocation();
  }
  componentDidMount() {}
  componentWillUpdate(nextProps, nextState) {}
  componentDidUpdate(prevProps, prevState) {}

  render() {
    //console.log(this.context.introVisible);
    const context = this.context;

    const mapActiveClass = context.mapVisible
      ? "res-map full-view active"
      : "res-map full-view";
    const mapStyles = {
      width: "100%",
      height: "100%",
      backgroundColor: "hsla(0, 0%, 0%, 0)"
    };

    Commute.defaultProps = {
      mapStyles: [
        {
          featureType: "all",
          elementType: "all",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "administrative.country",
          elementType: "all",
          stylers: [{ visibility: "on" }]
        },
        {
          featureType: "administrative.country",
          elementType: "labels.text.fill",
          stylers: [{ color: "#ff0000" }]
        },
        {
          featureType: "administrative.country",
          elementType: "labels.text.stroke",
          stylers: [{ color: "#3a3535" }]
        },
        {
          featureType: "administrative.province",
          elementType: "all",
          stylers: [{ visibility: "on" }]
        },
        {
          featureType: "administrative.province",
          elementType: "labels.text.fill",
          stylers: [{ color: "#ff0000" }]
        },
        {
          featureType: "administrative.province",
          elementType: "labels.text.stroke",
          stylers: [{ color: "#3a3535" }]
        },
        {
          featureType: "administrative.locality",
          elementType: "all",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "administrative.locality",
          elementType: "labels.text.fill",
          stylers: [{ color: "#ffffff" }]
        },
        {
          featureType: "administrative.locality",
          elementType: "labels.text.stroke",
          stylers: [
            { color: "#756151" },
            { lightness: "15" },
            { saturation: "2" },
            { gamma: "3.67" }
          ]
        },
        {
          featureType: "road.highway",
          elementType: "all",
          stylers: [{ visibility: "simplified" }]
        },
        {
          featureType: "road.highway",
          elementType: "geometry.fill",
          stylers: [{ visibility: "simplified" }, { color: "#f0af54" }]
        },
        {
          featureType: "road.highway",
          elementType: "labels.text",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "road.highway",
          elementType: "labels.icon",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "road.highway.controlled_access",
          elementType: "all",
          stylers: [{ visibility: "simplified" }]
        },
        {
          featureType: "road.highway.controlled_access",
          elementType: "geometry.fill",
          stylers: [{ visibility: "simplified" }, { color: "#f0af54" }]
        },
        {
          featureType: "road.highway.controlled_access",
          elementType: "labels.text",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "road.highway.controlled_access",
          elementType: "labels.icon",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "road.arterial",
          elementType: "all",
          stylers: [{ visibility: "simplified" }]
        },
        {
          featureType: "road.arterial",
          elementType: "geometry.fill",
          stylers: [{ visibility: "simplified" }, { color: "#d2c1a5" }]
        },
        {
          featureType: "road.arterial",
          elementType: "labels",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "road.local",
          elementType: "all",
          stylers: [{ visibility: "simplified" }]
        },
        {
          featureType: "road.local",
          elementType: "geometry.fill",
          stylers: [{ color: "#c4a983" }, { visibility: "simplified" }]
        },
        {
          featureType: "road.local",
          elementType: "labels",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "water",
          elementType: "all",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "water",
          elementType: "geometry",
          stylers: [{ visibility: "off" }]
        },
        {
          featureType: "water",
          elementType: "geometry.fill",
          stylers: [{ visibility: "off" }, { color: "#e3ce9f" }]
        },
        {
          featureType: "water",
          elementType: "geometry.stroke",
          stylers: [{ visibility: "simplified" }]
        },
        {
          featureType: "water",
          elementType: "labels",
          stylers: [{ visibility: "off" }]
        }
      ]
    };

    //console.log(this.props.google);
    /*
	map.mapTypes.set('styled_map', BobsMyUncle); // Get that sweet map style
	map.setMapTypeId('styled_map');
    */
    return (
      <div id="Map" className={mapActiveClass}>
        <div id="Commute">
          <Map
            google={this.props.google}
            zoom={14}
            style={mapStyles}
            initialCenter={context.myLatLng}
            disableDefaultUI={true}
            mapTypeControlOptions={{
              mapTypeIds: [
                "roadmap",
                "satellite",
                "hybrid",
                "terrain",
                "styled_map"
              ]
            }}
          >
            <Marker
              position={context.myLatLng}
              title="Me"
              animation={this.props.google.maps.Animation.DROP}
              icon={{
                url:
                  "https://icons.iconarchive.com/icons/icons-land/vista-map-markers/128/Map-Marker-Push-Pin-2-Right-Chartreuse-icon.png",
                scaledSize: new this.props.google.maps.Size(64, 64)
              }}
            />
          </Map>
        </div>
        <div className="res-map-info">
          <p className="thinking">Calculating my commute to you.</p>
        </div>
      </div>
    );
  }
}

//export default Commute;
export default GoogleApiWrapper({
  apiKey: "AIzaSyBl1YIXtw0RdoIw7Qo5h7Q32IIy3zkw6C0"
})(Commute);
