import { AppData } from "./appdata";
import Employer from "./employer";

const Employers = () => {
  let { state, dispatch } = React.useContext(AppData);

  const employerList = state.resume.history;

  const [employers, setEmployers] = useState(employerList);
  /*
  const [employers, setEmployers] = useState(employers);

  useEffect(() => {
    console.log("useEffect hook ran", employers);
    // runs on load and update
  }, [employers]);

  return (
    <div className="song-list">
      <ul>
        {songs.map(song => {
          return <li key={index}>{song.title}</li>;
        })}
      </ul>
      <NewSongForm addSong={addSong} />
      <button onClick={() => setAge(age + 1)}>Add 1 to age: {age}</button>
    </div>
  );
*/
  return (
    <React.Fragment>
      {employers.map((employer, index) => {
        return <Employer key={index} employer={employer} />;
      })}
    </React.Fragment>
  );
};

export default Employers;
