class Intro extends React.Component {
  static contextType = AppData; // ***** access via this.context

  state = {};
  constructor(props) {
    super(props);
    this.state = {
      introVisible: false,
      steps: [],
      headers: [],
      totalSteps: 0,
      currentStep: 0,
      nextStepper: null,
      previousStepper: null,
      statusEl: null,
      statusText: "loading...",
      nextTextEl: null,
      prevTextEl: null,
      nextText: "",
      previousText: ""
    };

    this.closeIntro = this.closeIntro.bind(this);
    this.nextStep = this.nextStep.bind(this);
    this.previousStep = this.previousStep.bind(this);
    this.updateStatus = this.updateStatus.bind(this);
  }

  /*
  stepOnIt(event){ // $(document).on('click', '.step-off h2 a', function(e) {
    event.preventDefault();

    $('.section-'+currentStep).removeClass('current');
    $(this).is('.step-prev') ? currentStep-- : currentStep++;
    $('.section-'+currentStep).addClass('current');
    $('.step-off').attr('data-current-section', currentStep);
    $('.step-off .status').text(currentStep + ' / ' + $('.step-off .step').length);
    stepAlong();
  }
  getSteppin(){
    $('.step-off').attr('data-current-section', currentStep);
    $('.step-off .section-1').addClass('current');
    $('.step-off .status').text('1 / ' + $('.step-off .step').length);
    stepAlong();
  }
  stepAlong(){
    $('.step-off a.step-prev, .step-off a.step-next').hide();
    if (currentStep > 1){
      prevTitle = $('.step-off .section-' + (currentStep - 1) + ' header').text();
      $('.step-off a.step-prev').show();
    } else {
      prevTitle = '';
    }
    if (currentStep < $('.step-off .step').length){
      nextTitle = $('.step-off .section-' + (currentStep + 1) + ' header').text();
      $('.step-off a.step-next').show();
    } else {
      nextTitle = '';
    }
    $('.step-off a.step-prev i').text(prevTitle);
    $('.step-off a.step-next i').text(nextTitle);
  }
*/

  componentDidMount() {
    const widget = ReactDOM.findDOMNode(this);
    const controls = widget.getElementsByClassName("step-o-matic")[0];
    const allSteps = widget.getElementsByClassName("step");
    const allHeaders = widget.getElementsByClassName("step-title");
    const nextEl = controls.getElementsByClassName("step-next")[0];
    const prevEl = controls.getElementsByClassName("step-prev")[0];
    const nextTextEl = nextEl.querySelector("i");
    const prevTextEl = prevEl.querySelector("i");
    const statusEl = controls.getElementsByClassName("status")[0];
    //console.log(allHeaders[0]);
    this.setState({
      totalSteps: allSteps.length,
      steps: allSteps,
      headers: allHeaders,
      nextText: allHeaders[1].innerText,
      nextStepper: nextEl,
      previousStepper: prevEl,
      statusEl: statusEl,
      statusText: "...",
      nextTextEl: nextTextEl,
      prevTextEl: prevTextEl
    });
    this.updateStatus();
  }
  componentDidUpdate(prevProps, prevState) {
    //document.body.classList.remove(prevState.currentTheme);
    //document.body.classList.add(this.state.currentTheme);
  }

  closeIntro(event) {
    const intro = document.getElementById("HEY");
    const menuItem = document.getElementsByClassName("btfu")[0];
    intro.classList.remove("active");
    menuItem.classList.remove("active");
    this.setState({ introVisible: false });
  }
  nextStep(event) {
    event.preventDefault();
    const nextTextEl = event.target.querySelector("i");
    //console.log(nextText);
  }
  previousStep(event) {
    event.preventDefault();
    const prevTextEl = event.target.querySelector("i");
    //console.log(nextText);
  }
  updateStatus(event) {
    console.log("can has update?");

    /*
  stepAlong(){
    $('.step-off a.step-prev, .step-off a.step-next').hide();
    if (currentStep > 1){
      prevTitle = $('.step-off .section-' + (currentStep - 1) + ' header').text();
      $('.step-off a.step-prev').show();
    } else {
      prevTitle = '';
    }
    if (currentStep < $('.step-off .step').length){
      nextTitle = $('.step-off .section-' + (currentStep + 1) + ' header').text();
      $('.step-off a.step-next').show();
    } else {
      nextTitle = '';
    }
    $('.step-off a.step-prev i').text(prevTitle);
    $('.step-off a.step-next i').text(nextTitle);
  }
