import * as React from "react";
import { AppData } from "./appdata";

class Menu extends React.Component {
  static displayName = "AppMenu";
  static contextType = AppData; // ***** access via this.context
  //console.log()
  constructor(props) {
    super(props);
    this.state = {
      currentTheme: "default",
      introVisible: null,
      menuVisible: null,
      myGlobalFunction: {},
      toggleIntro: null
    };

    this.toggleTheme = this.toggleTheme.bind(this);
    this.showMenu = this.showMenu.bind(this);
  }
  componentDidMount() {
    //this.setState({ introVisible: this.context.introVisible });
    //this.setState({ menuVisible: this.context.menuVisible });
    //this.context.introVisible.subscribe(this.handleIntroChange);
  }
  componentDidUpdate(prevProps, prevState) {
    document.body.classList.remove(prevState.currentTheme);
    document.body.classList.add(this.state.currentTheme);
  }

  toggleTheme(event, newClass, elm) {
    console.log(elm);
    const buttonLI = event.target.closest("li");
    if (this.state.currentTheme == newClass) {
      this.setState({ currentTheme: "default" });
      buttonLI.classList.remove("active");
    } else {
      this.setState({ currentTheme: newClass });
      buttonLI.classList.add("active");
    }
    /*
      if (!agileTheme){
        getMessy($('.agile .rr'), false);
        kill slideshow for Interests
      } else {
        getMessy($('.agile .rr'), true);
        init slideshow for Interests
      }
    */
  }
  showMenu(event) {
    const opener = event.target.closest("section");
    // this is how you get pseudo-classes to take events ;)
    const noClicky = event.target.closest("ul");
    if (!noClicky) {
      this.setState({ menuVisible: !this.state.menuVisible });
      opener.classList.toggle("active");
    }
  }

  render() {
    const { toggleIntro } = this.context;
    return (
      <AppData.Consumer>
        {context => {
          const pdfLink = process.env.PUBLIC_URL + "/assets/resume.pdf";
          const menuActiveClass = context.menuVisible
            ? "contact-info active"
            : "contact-info";
          const introActiveClass = context.introVisible
            ? "tooltip btfu active"
            : "tooltip btfu";
          const themeActiveClass =
            context.currentTheme == "agile"
              ? "theme-toggle tooltip toggler theme active"
              : "theme-toggle tooltip toggler theme";
          return (
            <section
              className={menuActiveClass}
              onClick={e => {
                this.showMenu(e);
              }}
            >
              <h2 className="visually-hidden">Main Menu</h2>
              <ul>
                <li className="just-the-tip"></li>
                <li
                  className={themeActiveClass}
                  data-title="Agile theme on/off"
                  onClick={e => {
                    this.toggleTheme(e, "agile", "something");
                  }}
                >
                  <button
                    className="test-me"
                    data-test-name="Menu: Local"
                    data-test-cat="Theme Toggle"
                  >
                    <i className="fas fa-toggle-on"></i>
                    <i className="fas fa-toggle-off"></i>
                    <span>Agile theme toggle</span>
                  </button>
                </li>
                <li
                  className={introActiveClass}
                  data-title="Back to the beginning"
                  onClick={e => {
                    toggleIntro(e);
                    //context.toggleIntro(e, !this.state.introVisible);
                  }}
                >
                  <button
                    className="test-me"
                    data-test-name="Menu: Local"
                    data-test-cat="Introduction Link"
                  >
                    <i className="fas fa-circle-notch"></i>
                    <span>View Introduction (again)</span>
                  </button>
                </li>
                <li className="tooltip sigh" data-title="Pretty (old) things">
                  <button
                    className="test-me"
                    data-test-name="Menu: Local"
                    data-test-cat="Portfolio Link"
                  >
                    <i className="fas fa-camera-retro"></i>
                    <span>Old Screencaps</span>
                  </button>
                </li>

                <li
                  className="wrap tooltip"
                  data-title="Check out my 1-page resume"
                >
                  <a
                    href={pdfLink}
                    target="_blank"
                    className="pdf test-me"
                    data-test-name="Menu: Local"
                    data-test-cat="View PDF Resume"
                  >
                    <i className="far fa-file-pdf"></i>
                    <span>
                      Resum&#233;
                      <sup>
                        <i className="fas fa-external-link-alt"></i>
                      </sup>
                    </span>
                  </a>
                </li>

                <li
                  className="wrap tooltip"
                  data-title="View my LinkedIn profile"
                >
                  <a
                    href="https://www.linkedin.com/in/jeffrey-hill-ux-guru/"
                    target="_blank"
                    className="linkedin test-me"
                    data-test-name="Menu: Non-Local"
                    data-test-cat="View LinkedIn Profile"
