import React, { useState, useEffect } from "react";
import { AppData } from "./appdata";
import Employer from "./employer";

class Employers extends React.Component {
  static contextType = AppData; // ***** access via this.context

  constructor(props) {
    super(props);
    this.state = {
      balls: ""
    };

    this.toggleDetails = this.toggleDetails.bind(this);
  }

  componentDidMount() {
    const balls = "balls";

    this.setState({
      balls: balls
    });
  }
  componentWillUpdate(nextProps, nextState) {}
  componentDidUpdate(prevProps, prevState) {}

  toggleDetails(event) {}

  render() {
    const context = this.context;
    const employerList = context.resume.history;

    return (
      <React.Fragment>
        {employerList.map((employer, index) => {
          return <Employer key={index} id={index} employer={employer} />;
        })}
      </React.Fragment>
    );
  }
}

export default Employers;
