import * as React from "react";
import Resume from "../assets/js/resume";

const resumeData = Resume;

let AppData = React.createContext();

let initialState = {
  resume: resumeData,
  version: "0.6.6.6 OMeGa",
  tts: false,
  themes: ["default", "agile"],
  currentTheme: "",
  introVisible: true,
  qsObj: false,
  urlParams: new URLSearchParams(window.location.search),
  rotationClasses:
    "rr-7 rr-6 rr-5 rr-4 rr-3 rr-2 rr-1 rr0 rr1 rr2 rr3 rr4 rr5 rr6 rr7",
  polyClasses: "poly-0 poly-1 poly-2 poly-3 poly-4",
  $carousel: null,
  isFlickity: false,
  map: null,
  myLatLng: { lat: 42.4316604, lng: -83.484051 },
  testLL: { lat: 42.279594, lng: -83.732124 }, // AA
  tabTypes: ["chart", "table", "stars", "info"],
  tabIcons: ["chart-pie", "table", "star-half-alt", "info-circle"],
  tabCounter: 0,
  myGlobalFunction: e => {
    console.log("global function called for: " + e.target.nodeName);
  },
  increment: () => {
    this.setState({ num: this.state.num + 1 });
  },
  decrement: () => {
    this.setState({ num: this.state.num - 1 });
  },
  reset: () => {
    this.setState({ num: this.num });
  },
  whoWantsABodyMassage: () => {
    document.body.classList.add("loaded");
    document.body.classList.remove("loading");
  }
};

let reducer = (state, action) => {
  switch (action.type) {
    case "reset":
      return initialState;
    case "increment":
      return { ...state, count: state.count + 1 };
    case "decrement":
      return { ...state, count: state.count - 1 };
    case "set-color":
      return { ...state, currentColor: action.payload };
    case "increment-tab-index":
      return { ...state, tabCounter: state.tabCounter + 1 };
    case "toggle-theme":
      console.log(action.payload);
      // bool: !this.state.bool
      // hasClass(element, className)
      //return document.body.classList.toggle("agile", state.isAgile);
      return { ...state, currentTheme: action.payload };
    default:
      return null;
  }
};

function AppDataProvider(props) {
  let [state, dispatch] = React.useReducer(reducer, initialState);
  let value = { state, dispatch };

  return <AppData.Provider value={value}>{props.children}</AppData.Provider>;
}

let AppDataConsumer = AppData.Consumer;

export { AppData, AppDataProvider, AppDataConsumer };
