import React, { useState, useEffect } from "react";
import { AppData } from "./appdata";
import helpers from "./helpers";

const Employer = props => {
  let { state, dispatch } = React.useContext(AppData);
  const employer = props.employer;
  const offset = props.index * 80;
  const logoClass = "logo sprite-offset-" + offset + " rr";
  const logoData = "assets/img/logos/" + employer.logo;
  const linkTitle = "Open " + employer.name + " in a new tab";
  console.log(props.index);
  //let classy = str => dispatch({ type: "classify" });
  /*
			class: 			classify(resume.history[x].name),
			name: 			resume.history[x].name,
			logo: 			resume.history[x].logo,
			offset: 		(y*80),
			location: 		resume.history[x].address,
			url: 			resume.history[x].url,
			title: 			resume.history[x].title,
			start: 			formatMonthYear(resume.history[x].start),
			end: 			formatMonthYear(resume.history[x].end),
			employment: 	resume.history[x].employment,
			description: 	resume.history[x].description,
			els: 			resume.history[x].details,
			sks: 			shuffle(resume.history[x].skills),
			fws: 			resume.history[x].frameworks,
			ims: 			resume.history[x].projects
  */
  return (
    <React.Fragment>
      <article className="res-editable res-organization {helpers.classify(employer.name)}">
        <nav>
          <button className="editable">
            <span className="tooltip" data-title="Turn editing on/off.">
              Edit
            </span>
          </button>
          <button className="print-basket">
            <span
              className="tooltip"
              data-title="Add/Remove this for printing."
            >
              Add for Print
            </span>
          </button>
        </nav>
        <h3 className="org-title">
          Role: <span className="blank can-edit">{employer.title}</span>
          <i className="inner"></i>
        </h3>

        <h4 className="org-name">
          <span className={logoClass} data={logoData}></span>
          Org:
          <span className="blank">
            <a
              href={employer.url}
              data-title={linkTitle}
              target="_blank"
              className="org-url tooltip can-edit"
            >
              {employer.name}
            </a>
          </span>
        </h4>
        <address className="org-location can-edit">{employer.location}</address>
        <div className="org-dates">
          <time
            className="org-start can-edit"
            dangerouslySetInnerHTML={{ __html: employer.start }}
          ></time>
          <span> to </span>
          <time
            className="org-end can-edit"
            dangerouslySetInnerHTML={{ __html: employer.end }}
          ></time>
        </div>
        <span className="org-capacity">
          Capacity:
          <span className="blank can-edit">{employer.employment}</span>
        </span>
        <details className="org-details">
          <summary className="org-summary add-tack rr">
            <i className="fas fa-caret-up"></i>
            <i className="fas fa-caret-down"></i>
            <span className="show-toggle show-more">Show More</span>
            <span className="show-toggle show-less">Show Less</span>
          </summary>
          <div className="guts can-edit">
            <h5>About {employer.name}</h5>
            <div
              dangerouslySetInnerHTML={{ __html: employer.description }}
            ></div>

            <h5 className="org-highlights">
              Highlights &amp; Responsibilities
            </h5>
            <ul className="org-accomplishments custom-list">
              <li>...</li>
            </ul>

            <h5 className="org-skills">Skills, Tools...</h5>
            <ul className="org-skill-list werd-cloud">
              <li>...</li>
            </ul>

            <div className="gallery">
              <h5>Portfolio</h5>
              <ul className="polaroids clearfix">
                <li>...</li>
              </ul>
            </div>

            {/*

Handlebars.registerHelper("gy", function(ims) {
	var result = "";
	for (x in ims) {
		var template = Handlebars.compile(galleryTemplate);
		if (ims[x].thumb && ims[x].thumb !== ""){
			result += template({ 
				thumb: 			ims[x].thumb,
				classy: 		classify(ims[x].name),
				details: 		ims[x].details,
				title: 			ims[x].description
			});
		}
	}
	return new Handlebars.SafeString(result);
});
