        <GoogleMapReact
          bootstrapURLKeys={{ key: "AIzaSyBl1YIXtw0RdoIw7Qo5h7Q32IIy3zkw6C0" }}
          defaultCenter={this.props.center}
          defaultZoom={this.props.zoom}
