import React, { useState, useEffect } from "react";
import { AppData } from "./appdata";

const Gallery = props => {
  let { state, dispatch } = React.useContext(AppData);
  const gallery = props.gallery;
  console.log(gallery);
  return (
    <React.Fragment>
      <ul className="polaroids clearfix">
        {gallery.map((item, index) => {
          const tipTitle = "Open " + item.title + " in a new tab";
          const imagePath = "assets/img/portfolio/" + item.thumb;
          return (
            <li class="tooltip" data-title={tipTitle}>
              <a href={imagePath} class="rr" title={item.title} target="_blank">
                <span class="clippy">
                  <img
                    class={helpers.classify(item.name)}
                    src={imagePath}
                    alt={item.title}
                  />
                </span>
                <p dangerouslySetInnerHTML={{ __html: item.details }}></p>
              </a>
            </li>
          );
        })}
      </ul>
    </React.Fragment>
  );
};

export default Gallery;

