import * as React from "react";
import { AppData } from "./appdata";

/*
var themeSwitcher = function(theme, apply){
	if (!apply){
		getMessy($('.agile .rr'), false);
		$('body').removeClass(theme);
		currentTheme = '';
		$carousel.flickity('destroy');
	} else {
		$('body').addClass(theme);
		getMessy($('.agile .rr'), true);
		currentTheme = theme;
		$carousel = $('#Interests ul').flickity({
			// options
			cellAlign: 'left',
			contain: true,
			wrapAround: true,
			autoPlay: 5000,
			pauseAutoPlayOnHover: true,
			fade: true,
			prevNextButtons: false
		});
	}
	isFlickity = apply;
}
*/

class Menu extends React.Component {
  static appContext = AppData;
  constructor(props) {
    super(props);
    this.state = {
      currentTheme: "default",
      introVisible: false,
      menuVisible: false
    };

    this.toggleTheme = this.toggleTheme.bind(this);
    this.showMenu = this.showMenu.bind(this);
    this.showIntro = this.showIntro.bind(this);
    this.updateValue = this.updateValue.bind(this);
  }

  componentDidUpdate(prevProps, prevState) {
    document.body.classList.remove(prevState.currentTheme);
    document.body.classList.add(this.state.currentTheme);
  }

  updateValue = event => {
    this.setState(event);
  };

  toggleTheme(event, newClass, elm) {
    console.log(elm);
    const buttonLI = event.target.closest("li");
    if (this.state.currentTheme == newClass) {
      this.setState({ currentTheme: "default" });
      buttonLI.classList.remove("active");
    } else {
      this.setState({ currentTheme: newClass });
      buttonLI.classList.add("active");
    }
    this.props.context.updateValue("myGlobalFunction", event);
  }
  showMenu(event) {
    const opener = event.target.closest("section");
    const noClicky = event.target.closest("ul");
    if (!noClicky) {
      opener.classList.toggle("active");
    }
  }
  showIntro(event) {
    document.getElementById("HEY").classList.add("active");
  }

  render() {
    return (
      <section
        className="contact-info"
        onClick={e => {
          this.showMenu(e);
        }}
      >
        <h2 className="visually-hidden">Main Menu</h2>
        <ul>
          <li className="just-the-tip"></li>
          <li
            className="theme-toggle tooltip toggler theme"
            data-title="Agile theme on/off"
            onClick={e => {
              this.toggleTheme(e, "agile", "something");
            }}
          >
            <button
              className="test-me"
              data-test-name="Menu: Local"
              data-test-cat="Theme Toggle"
            >
              <i className="fas fa-toggle-on"></i>
              <i className="fas fa-toggle-off"></i>
              <span>Agile theme toggle</span>
            </button>
          </li>
          <li
            className="tooltip btfu"
            data-title="Back to the beginning"
            onClick={e => {
              this.showIntro(e);
            }}
          >
            <button
              className="test-me"
              data-test-name="Menu: Local"
              data-test-cat="Introduction Link"
            >
              <i className="fas fa-circle-notch"></i>
              <span>View Introduction (again)</span>
            </button>
          </li>
          <li className="tooltip sigh" data-title="Pretty (old) things">
            <button
              className="test-me"
              data-test-name="Menu: Local"
              data-test-cat="Portfolio Link"
            >
              <i className="fas fa-camera-retro"></i>
              <span>Old Screencaps</span>
            </button>
          </li>

          <li className="wrap tooltip" data-title="Check out my 1-page resume">
            <a
              href="assets/resume.pdf"
              target="_blank"
              className="pdf test-me"
              data-test-name="Menu: Local"
              data-test-cat="View PDF Resume"
            >
              <i className="far fa-file-pdf"></i>
              <span>
                Resum&#233;
                <sup>
                  <i className="fas fa-external-link-alt"></i>
                </sup>
              </span>
            </a>
          </li>

          <li className="wrap tooltip" data-title="View my LinkedIn profile">
            <a
              href="https://www.linkedin.com/in/jeffrey-hill-ux-guru/"
              target="_blank"
              className="linkedin test-me"
              data-test-name="Menu: Non-Local"
              data-test-cat="View LinkedIn Profile"
            >
              <i className="fab fa-linkedin"></i>
              <span>
                LinkedIn
                <sup>
                  <i className="fas fa-external-link-alt"></i>
                </sup>
              </span>
            </a>
          </li>

          <li className="tooltip" data-title="Call me, maybe">
            <a
              href="tel:phone"
              className="phone test-me"
              data-test-name="Menu: Local"
              data-test-cat="Phone Link"
            >
              <i className="fas fa-mobile-alt"></i>
              <span>prettyPhone</span>
