class Commute extends React.Component {
  static contextType = AppData; // ***** access via this.context

  constructor(props) {
    super(props);
    this.state = {
      mapIsReady: false,
      myLoc: {},
      googleMap: {},
      jeffMarker: {},
      jeffInfo: {}
    };
    //const google = window.google;
    this.updateMapStyle = this.updateMapStyle.bind(this.map, this.marker);
  }
  componentWillMount() {
    window.onload = this.setState({
      mapIsReady: true,
      myLoc: this.context.myLatLng
    });
  }
  componentDidMount() {
    if (this.state.mapIsReady) {
      // Display the map
      this.map = new google.maps.Map(document.getElementById("Commute"), {
        center: this.state.myLoc,
        zoom: 14,
        disableDefaultUI: true,
        backgroundColor: "hsla(0, 0%, 0%, 0)",
        styles: BobsMyUncle
      });
      var map = this.map;
      console.log("We have a map, set marker, etc...");
      // You also can add markers on the map below

      var contentString = '<div id="mapContent"><h4>Me</h4></div>';
      this.infowindow = new google.maps.InfoWindow({
        content: contentString
      });
      var infowindow = this.infowindow;
      this.marker = new google.maps.Marker({
        position: { lat: 42.4316604, lng: -83.484051 },
        map: map,
        title: "Me",
        animation: google.maps.Animation.DROP,
        icon: {
          url:
            "https://icons.iconarchive.com/icons/icons-land/vista-map-markers/128/Map-Marker-Push-Pin-2-Right-Chartreuse-icon.png",
          scaledSize: new google.maps.Size(64, 64)
        }
      });
      var marker = this.marker;
      google.maps.event.addListener(marker, "click", function() {
        infowindow.open(map, this);
      });

      this.setState({
        googleMap: map,
        jeffMarker: marker,
        jeffInfo: infowindow
      });
      setTimeout(function() {
        //this.updateMapStyle = (map, marker);
      }, 3000);
      //this.updateMapStyle(map, marker);
    }
  }

  componentWillUpdate(nextProps, nextState) {
    console.log("Component Will Update");
  }
  componentDidUpdate(prevProps, prevState) {
    console.log("Component Did Update");
    //this.updateMapStyle = (this.map, this.marker);
  }

  createMarker = () => {
    console.log(this.googleMapRef.current);
    //const myMarker = new google.maps.Marker({
    new google.maps.Marker({
      position: { lat: 42.4316604, lng: -83.484051 },
      setMap: this.googleMap,
      title: "Me",
      animation: google.maps.Animation.DROP,
      icon: {
        url:
          "https://icons.iconarchive.com/icons/icons-land/vista-map-markers/128/Map-Marker-Push-Pin-2-Right-Chartreuse-icon.png",
        scaledSize: new google.maps.Size(64, 64)
      }
    });
    //marker.setMap(this.googleMapRef.current);
    //this.setState({ jeffMarker: myMarker });
  };

  render() {
    //console.log(this.context.introVisible);
    const context = this.context;

    const mapActiveClass = context.mapVisible
      ? "res-map full-view active"
      : "res-map full-view";

    return (
      <div id="Map" className={mapActiveClass}>
        <div id="Commute" style={{ width: "100%", height: "100%" }}></div>
        <div className="res-map-info">
          <p className="thinking">Calculating my commute to you.</p>
        </div>
      </div>
    );
  }
}

export default Commute;
